const selector=document.querySelectorAll(".selector"),cataloghover=e=>{e.srcElement.nextSibling.nextSibling.classList.add("catalog-border-active")};selector.forEach((e=>{e.addEventListener("mouseover",cataloghover)}));const catalogunhover=e=>{e.srcElement.nextSibling.nextSibling.classList.remove("catalog-border-active")};selector.forEach((e=>{e.addEventListener("mouseout",catalogunhover)}));