const listItems=document.querySelectorAll(".listItem a"),ListItemhover=t=>{const e=t.srcElement,s=e.previousSibling;e.classList.add("listItem-activ"),s.classList.add("border-activ")};listItems.forEach((t=>{t.addEventListener("mouseover",ListItemhover)}));const ListItemUnhover=t=>{const e=t.srcElement,s=e.previousSibling;e.classList.remove("listItem-activ"),s.classList.remove("border-activ")};listItems.forEach((t=>{t.addEventListener("mouseout",ListItemUnhover)}));